import React, { useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, 
    IonInput, IonItem, IonLabel, IonList, IonItemDivider, IonDatetime } from '@ionic/react';
import './Tab1.css';


var today = new Date();

const todaysDate = () => {
    return today.toDateString(); 
};

const Tab1: React.FC = () => {

    const [text, setText] = useState<string>();
    const [number, setNumber] = useState<number>();


    return (
        <IonPage>
          <IonHeader>
            <IonToolbar>
              <IonTitle>Enter Data</IonTitle>
            </IonToolbar>
          </IonHeader>
          <IonContent>
            <IonList>

              <IonItem>
                <IonLabel position="floating">Name:</IonLabel>
                <IonInput value={text} onIonChange={e => setText(e.detail.value!)}></IonInput>
              </IonItem>


              <IonItem>
                <IonLabel position="stacked">Hours Worked:</IonLabel>
                <IonInput type="number" value={number} onIonChange={e => setNumber(parseInt(e.detail.value!, 10))}></IonInput>
              </IonItem>

              <IonItemDivider>Date</IonItemDivider>

              <IonItem>

                <IonLabel ></IonLabel>
                <IonDatetime placeholder={todaysDate()}></IonDatetime>
              </IonItem>

              <IonItem>
                <IonLabel position="floating">Floating Label</IonLabel>
                <IonInput value={text}></IonInput>
              </IonItem>

              <IonItem>
                <IonLabel position="fixed">Fixed Label</IonLabel>
                <IonInput value={text}></IonInput>
              </IonItem>

              <IonItem>
                <IonLabel position="stacked">Stacked Label</IonLabel>
                <IonInput value={text}> </IonInput>
              </IonItem>
            </IonList>
          </IonContent>
        </IonPage>
      );
};

export default Tab1;
