import { Redirect, Route } from 'react-router-dom';
import {
  IonApp,
  IonIcon,
  IonLabel,
  IonRouterOutlet,
  IonTabBar,
  IonTabButton,
  IonTabs,
} from '@ionic/react';
import { IonReactRouter } from '@ionic/react-router';
import { ellipse, square, list } from 'ionicons/icons';
import DataInput from './pages/DataInput';
import Tab2 from './pages/Tab2';
import Tab3 from './pages/Tab3';

/* Core CSS required for Ionic components to work properly */
import '@ionic/react/css/core.css';

/* Basic CSS for apps built with Ionic */
import '@ionic/react/css/normalize.css';
import '@ionic/react/css/structure.css';
import '@ionic/react/css/typography.css';

/* Optional CSS utils that can be commented out */
import '@ionic/react/css/padding.css';
import '@ionic/react/css/float-elements.css';
import '@ionic/react/css/text-alignment.css';
import '@ionic/react/css/text-transformation.css';
import '@ionic/react/css/flex-utils.css';
import '@ionic/react/css/display.css';

/* Theme variables */
import './theme/variables.css';

//Firebase import
import firebase from 'firebase/app';
var firebaseConfig = {
    apiKey: "AIzaSyCWZYjRtvdP7VTVuefFOnmhPLClCVATR0k",
    authDomain: "volunteer-hours-f5807.firebaseapp.com",
    projectId: "volunteer-hours-f5807",
    storageBucket: "volunteer-hours-f5807.appspot.com",
    messagingSenderId: "466587075836",
    appId: "1:466587075836:web:880d534180e7beb6a38379",
    measurementId: "G-8CEWGB3S8M"
  };
firebase.initializeApp(firebaseConfig);

const App: React.FC = () => {
    const onEntry = (newName: string, newHours: number, newDate: string) => {

        if(newName) {
            if(newDate.charAt(0) <='9' && newDate.charAt(0) >= '0') {
                const loseTime = newDate.split('T');
                console.log("Data Output: Name: " + newName + ", Hours: " + newHours + ", Date: " + loseTime[0] );
            }
            else {
                const words = newDate.split(' ');
                var numMon = "JanFebMarAprMayJunJulAugSepOctNovDec".indexOf(words[1]) / 3 + 1;
                console.log(words[3] + "-" + words[2] + "-" + numMon);
            }
        }
        else {
            alert("Name Field")
        }
    }
    return (
        <IonApp>
            <IonReactRouter>
            <IonTabs>
                <IonRouterOutlet>
                <Route exact path="/tab1">
                    <DataInput
                     onSubmit={onEntry}
                    >   
                    </DataInput>
                </Route>
                <Route exact path="/tab2">
                    <Tab2 />
                </Route>
                <Route path="/tab3">
                    <Tab3 />
                </Route>
                <Route exact path="/">
                    <Redirect to="/tab1" />
                </Route>
                </IonRouterOutlet>
                <IonTabBar slot="bottom">
                <IonTabButton tab="tab1" href="/tab1">
                    <IonIcon icon={list} />
                    <IonLabel>Tab 1</IonLabel>
                </IonTabButton>
                <IonTabButton tab="tab2" href="/tab2">
                    <IonIcon icon={ellipse} />
                    <IonLabel>Tab 2</IonLabel>
                </IonTabButton>
                <IonTabButton tab="tab3" href="/tab3">
                    <IonIcon icon={square} />
                    <IonLabel>Tab 3</IonLabel>
                </IonTabButton>
                </IonTabBar>
            </IonTabs>
            </IonReactRouter>
        </IonApp>
    );
};
export default App;
