import React, { useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, 
    IonInput, IonItem, IonLabel, IonList, IonButton, IonDatetime } from '@ionic/react';
import { Controller, useForm } from 'react-hook-form';
import './DataInput.css';

interface TextEntryProps {
    onSubmit: (name: string, hours: number, date: string) => void;
}

var today = new Date();

const todaysDate = () => {
    return today.toDateString(); 
};

const getValid = async (data: any) => {
    console.log("test!")
}

const DataInput: React.FC<TextEntryProps> = (props: TextEntryProps) => {
    const {control, handleSubmit } = useForm();
    const [text, setText] = useState<string>();
    const [number, setNumber] = useState<number>();
    const [date, setDate] = useState<string>();


    return (
        <IonPage>
          <IonHeader>
            <IonToolbar>
              <IonTitle>Enter Data</IonTitle>
            </IonToolbar>
          </IonHeader>
          <IonContent>
            <IonList>

              <IonItem>
                <IonLabel position="stacked">Name:</IonLabel>
                <IonInput value={text} onIonChange={e => setText(e.detail.value!)}></IonInput>
              </IonItem>


              <IonItem>
                <IonLabel position="stacked">Hours Worked:</IonLabel>
                <IonInput type="number" value={number} onIonChange={e => setNumber(parseInt(e.detail.value!, 10))}></IonInput>
              </IonItem>

              <IonItem>

                <IonLabel position="stacked">Date:</IonLabel>
                <IonDatetime placeholder={todaysDate()} value={date} onIonChange={e => setDate(e.detail.value!)}></IonDatetime>
              </IonItem>


            </IonList>
            <form onSubmit={handleSubmit(getValid)}>
                <IonButton type="submit">Submit</IonButton>
            </form>
          </IonContent>
        </IonPage>
      );
};

export default DataInput;
